package com.jasonz.leetcode.arrayAndString;


import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class CountUniqueCharactersTest{
    CountUniqueCharacters ins = new CountUniqueCharacters();

    @Test
    public void testUniqueLetterString() {
        String s = "";
        int expected = 0;
        int actual = 0;


        s = "ABC";
        expected=10;
        actual = ins.uniqueLetterStringOptimal(s);
        assertEquals(expected, actual);

        s= "ABA";
        expected=8;
        actual = ins.uniqueLetterString(s);
        assertEquals(expected, actual);

        s="LEETCODE";
        expected=92;
        actual = ins.uniqueLetterString(s);
        assertEquals(expected, actual);

//        s ="";
//        expected=628539;
//        actual = ins.uniqueLetterString(s);
//        assertEquals(expected, actual);


    }
}